const express = require('express');
const cors = require('cors');
const app = express();
var http = require('http');
const fs = require("fs");
var httpServer = http.createServer(app);

require("./model/DataBaseInit");

// 路由分发
const userRouter = require('./route/Users');

app.all('*', function (req: any, res: { setHeader: (arg0: string, arg1: string) => void; }, next: () => void) {
    res.setHeader('Access-Control-Expose-Headers', 'Authorization');
    res.setHeader('Access-Control-Expose-Headers', 'token');
    next();
})

// token校验
app.use(require("./utils/tokenMolliz"));

//允许处理from表单过来的数据
app.use(express.urlencoded({
    extended: true
}))

//允许处理JSON数据
app.use(express.json());

//配置全局跨域
const whitelist = ['http://127.0.0.1']
const corsOptions = {
    origin: function (origin: string, callback: (arg0?: Error | null, arg1?: boolean | undefined) => void) {
        if (whitelist.indexOf(origin) !== -1) {
            console.log(origin)
            callback(null, true);
        } else {
            console.log(origin)
            callback(new Error('Not allowed by CORS'));
        }
    }
}
app.use(cors());

//静态资源入口
app.use(express.static(__dirname + '/public'));

//处理全局请求
app.use('/api/users', userRouter);

const port = 3000;
httpServer.listen(port, () => {
    console.log("http://127.0.0.1:" + port);
});