const { Sequelize } = require("sequelize");
require("mysql2");

const sq = new Sequelize("microblog_db", "root", "000000", {
    host: "http:127.0.0.1",
    dialect: 'mysql',
    logging: false
});

// 连接数据库
(async function () {
    try {
        await sq.authenticate();
        console.log('mysql is ready...');
    } catch (error) {
        console.error('Unable to connect to the database:', error);
    }
})()
module.exports = sq;
