const sequelizes = require("../index");
const { DataTypes } = require("sequelize");

const user = sequelizes.define("user_tb", {
    // UID
    uId: {
        type: DataTypes.STRING(100),
        allowNull: false,
        comment: "用户唯一ID"
    },
    // 用户名
    userName: {
        type: DataTypes.STRING(100),
        allowNull: true,
        comment: "用户账号"
    },
    // 密码
    passWord: {
        type: DataTypes.STRING(30),
        allowNull: true,
        comment: "用户密码"
    },
    // 电话号码
    phone: {
        type: DataTypes.STRING(11),
        allowNull: true,
        comment: "用户第二账号"
    },
    // 当前状态
    isUserState: {
        type: DataTypes.STRING(1),
        allowNull: true,
        defaultValue: "0",
        comment: "0未实名认证 1认证普通用户 2管理 3封禁"
    }
}, {
    updatedAt: false,
    createdAt: false,
    freezeTableName: true,
    paranoid: true //配置是否真正删除，如果为真只是添加一个删除时间
});

module.exports = user;