const client = require("./index");

const getRd = async (key: any) => {
    return JSON.parse(await client.get(key));
}

const setRd = async (key: any, val: any) => {
    const ins = await client.set(key, JSON.stringify(val));
    client.expire(key, (60 * 60 * 24));
    return ins;
}

const delRd = async (key: any) => {
    return await client.del(key);
}

module.exports = {
    getRd, setRd, delRd
}