const userExpress = require('express');
const router = userExpress.Router();

// 判断用户当前是否登录
router.get("/queryLoginState", async (req: any, res: { send: (arg0: { msg: any; flag: boolean; data?: any; }) => void; }, next: any) => {
    res.send({
        msg: '当前用户登陆状态正常',
        flag: true
    })
})

module.exports = router;