const {
    pathToRegexp
} = require('path-to-regexp');

/**
 *该模块负责token的认证，有些需要token的功能没有token直接报错 
 */

// 需要验证的名单
const needTokenApi = [{
    method: 'GET',
    path: '/api/user/queryLoginState1'
}];

//错误函数
function handleToken(req: any, res: any, next: any) {
    res.status(403).send({
        msg: 'no token',
        code: 403
    });
}

module.exports = (req: any, res: any, next: () => void) => {
    let token = req.headers.token;
    //看看needTokenApi有没有需要token认证的请 求
    const apis = needTokenApi.filter(api => {
        const reg = pathToRegexp(api.path);
        if (api.method === req.method && reg.test(req.path)) {
            return api.method === req.method && reg.test(req.path)
        }
    })
    // 如果你不是名单上的直接移交下一阶段
    if (apis.length === 0) {
        next();
        return;
    }

    //没有token认证的而且，在TokenApi名单上的
    if (!token) {
        handleToken(req, res, next);
        return;
    }
    //验证token OK
    next();
}